					["do_custom"] = true,
					["do_message"] = false,
				},
				["init"] = {
					["custom"] = "-- seconds to hold down ctrl to enable the release button\naura_env.TIMEOUT = 2\n\n-- should we auto-release if ctrl is held down long enough?\naura_env.AUTO_RELEASE = false\n\n-- format the text that explains this behavior\naura_env.INFO_TEXT = \"Hold CTRL for %d seconds to release.\"\naura_env.INFO_TEXT_SAFE = \"Keep holding and click the button.\"\n\n-- text shown below if someone can get back up and revive the group\naura_env.INFO_TEXT_HAS_RESS = \"|cffFF5555%d |4RESSER:RESSERS; CAN REVIVE EVERYONE!|r\"\naura_env.INFO_TEXT_HAS_RESS_NEARBY = \"|cffFF5555%d |4RESSER:RESSERS; CAN REVIVE YOU!|r\"\n\n-- instance ids\naura_env.INSTANCES = {\n    [1520] = 1, -- The Emerald Nightmare\n    [1648] = 1, -- Trial of Valor\n    [1530] = 1, -- The Nighthold\n    [1676] = 1, -- Tomb of Sargeras\n    [1712] = 1, -- Antorus the Burning Throne\n    [1861] = 1, -- Uldir\n}\n\n-- difficulty ids\naura_env.DIFFICULTIES = {\n    [8] = 1, -- Challenge Mode\n    [14] = 1, -- Normal (Raid)\n    [15] = 1, -- Heroic (Raid)\n    [16] = 1, -- Mythic (Raid)\n    -- [23] = 1, -- Mythic (Dungeon)\n}\n\n-- unit can revive others\nlocal function UnitCanReviveOthers(unit, cr)\n    local _, _, classID = UnitClass(unit)\n    return classID == 2 or classID == 5 or classID == 7 or classID == 10 or classID == 11 or (cr and classID == 9)\nend\n\n-- any class with self-revive that can revive the group?\nlocal function GetNumRessers()\n    local c, n = 0, 0\n    for unit in WA_IterateGroupMembers() do\n        if UnitIsDeadOrGhost(unit) then\n            if UnitHasIncomingResurrection(unit) and UnitCanReviveOthers(unit, true) then\n                c = c + 1\n            end\n        elseif UnitInRange(unit) and UnitCanReviveOthers(unit) then\n            n = n + 1\n        end\n    end\n    return c, n\nend\n\n-- copied from StaticPopup.lua#26\nlocal function GetSelfResurrectDialogOptions()\n    local resOptions = GetSortedSelfResurrectOptions()\n    if resOptions then\n        if IsEncounterLimitingResurrections() then\n            return resOptions[1], resOptions[2]\n        else\n            return resOptions[1]\n        end\n    end\nend\n\n-- returns true if we are in a instance or challenge mode (either one)\nfunction aura_env:IsRunningAllowed()\n    local _, _, diff, _, _, _, _, inst = GetInstanceInfo()\n    if inst and self.INSTANCES[inst] then\n        return false\n    elseif diff and self.DIFFICULTIES[diff] then\n        return false\n    end\n    return true\nend\n\n-- returns the popup widget\nfunction aura_env:GetPopup()\n    local _, sp = StaticPopup_Visible(\"DEATH\")\n    return sp\nend\n\n-- returns true if we can release and the button is enabled by default\nfunction aura_env:CanRelease()\n    local sp = self:GetPopup()\n    local allowed = true\n    if sp then\n        if IsFalling() or IsOutOfBounds() then\n            allowed = false -- falling or out of bounds\n        elseif IsEncounterSuppressingRelease() then\n            allowed = false -- encounter keeps us dead\n        elseif HasNoReleaseAura() then\n            allowed = false -- forced to stay dead\n        else\n            -- self ress options with potential restrictions\n            local option1, option2 = GetSelfResurrectDialogOptions()\n            if option1 and not option1.canUse then\n                allowed = false\n            end\n            if option2 and not option2.canUse then\n                allowed = false\n            end\n        end\n    end\n    return allowed, sp\nend\n\n-- returns true if we aren't restricted and can release\nfunction aura_env:IsReleaseAllowed()\n    local canRelease, sp = self:CanRelease()\n    if canRelease and sp then\n        -- is running not worth it?\n        local allowed = aura_env:IsRunningAllowed()\n        -- check if we have held ctrl long enough - if we aren't allowed to run\n        if not allowed then\n            local _, remain = aura_env:UpdateCtrlStatus()\n            if remain then\n                return remain < 1\n            end\n        end\n        -- is running allowed?\n        return allowed\n    end\n    return true\nend\n\n-- called every second in order to keep the button status up-to-date\nfunction aura_env:UpdateButton()\n    local canRelease, sp = self:CanRelease()\n    if canRelease then\n        -- update button status\n        local status = self:IsReleaseAllowed()\n        -- sp.button1:SetEnabled(status)\n        sp.button1:SetShown(status)\n        -- auto repop if the option is enabled\n        if status and aura_env.AUTO_RELEASE then\n            RepopMe()\n        end\n        -- append our explanation text and resize the popup widget\n        local text = sp.text:GetText()\n        if text then\n            local _, remain = aura_env:UpdateCtrlStatus()\n            local t\n            if remain and remain >= 1 then\n                t = format(self.INFO_TEXT, max(0, remain))\n            elseif remain then\n                t = self.INFO_TEXT_SAFE\n            else\n                t = format(self.INFO_TEXT, aura_env.TIMEOUT)\n            end\n            local c, n = GetNumRessers()\n            if n > 0 then\n                t = aura_env.INFO_TEXT_HAS_RESS_NEARBY:format(n) .. \"\\n\" .. t\n            elseif c > 0 then\n                t = aura_env.INFO_TEXT_HAS_RESS:format(c) .. \"\\n\" .. t\n            end\n            sp.text:SetText(t)\n            StaticPopup_Resize(sp, sp.which)\n        end\n        return true\n    end\n    return false\nend\n\n-- called to update ctrl status, returns also seconds elapsed\nfunction aura_env:UpdateCtrlStatus()\n    if IsControlKeyDown() then\n        if not aura_env.keyPressed then\n            aura_env.keyPressed = GetTime() + aura_env.TIMEOUT\n        end\n    else\n        aura_env.keyPressed = nil\n    end\n    local elapsed = aura_env.keyPressed\n    if elapsed then\n        return elapsed, elapsed - GetTime() + 1\n    end\n    return elapsed\nend",
