			["customText"] = "function(...)\n    local r = aura_env.region\n    local s = aura_env.state\n    local a = aura_env\n    if not r.count then\n        r.count = r.bar:CreateFontString(nil, \"OVERLAY\")\n        r.count:SetJustifyH(a.justifyH)\n        r.count:SetJustifyV(a.justifyV)\n        r.count:SetPoint(a.position, r.bar, a.position, a.shardX, a.shardY)\n        r.count:SetFont(r.text:GetFont())\n        r.count:SetTextColor(r.text:GetTextColor())\n        r.count:Show()\n    end\n    r.count:SetFont(r.text:GetFont())\n    r.count:SetTextColor(r.text:GetTextColor())\n    \n    if s and s.progressType == \"timed\" then\n        return\n    else\n        r.count:SetText((select(5, ...)))\n    end\nend\n\n\n\n",
