						["custom"] = "function(_, _, event, _, source, _, _, _, dest, _, _, _, spellID)\n    if source == WeakAuras.myGUID and spellID == 265412 and aura_env.TrackDoom then\n        local now = GetTime()\n        if event == \"SPELL_AURA_APPLIED\" then\n            aura_env.Doom[dest] = {\n                exp = GetTime() + 30, dur = 30, \n                expDot = GetTime() + 30, durDot = 30, \n                lastTick = 0, nextTick = 0\n            }\n            local d = aura_env.Doom[dest]\n            local nameplateCheck = false\n            for i = 1,40 do \n                if UnitGUID(\"nameplate\"..i) == dest then\n                    nameplateCheck = true\n                    local npd, npe = select(5, WA_GetUnitDebuff(\"nameplate\"..i, 265412, \"PLAYER\"))\n                    \n                    d.exp = npe\n                    d.dur = npd\n                    \n                    d.expDot = d.exp\n                    d.durDot = d.dur\n                    \n                    d.lastTick = 0\n                    d.nextTick = npe\n                    break\n                end\n            end\n            if nameplateCheck == false then\n                d.exp = GetTime() + 30\n                d.dur = 30\n                \n                d.expDot = d.exp\n                d.durDot = 30\n                \n                d.lastTick = 0\n            end\n        end\n        \n        if event == \"SPELL_AURA_REFRESH\" then\n            local d = aura_env.Doom[dest]\n            for i = 1,40 do \n                local nameplateCheck = false \n                if UnitGUID(\"nameplate\"..i) == dest then\n                    nameplateCheck = true\n                    local npd, npe = select(5, WA_GetUnitDebuff(\"nameplate\"..i, 265412, \"PLAYER\"))\n                    if d.lastTick > 0 then\n                        d.nextTick = d.lastTick + 30\n                    else\n                        d.nextTick = npe\n                        d.lastTick = d.exp\n                    end\n                    \n                    if d.dur ~= 30 then\n                        d.exp = d.lastTick + 30\n                        d.dur = npd\n                    end\n                    break\n                end\n            end\n            if nameplateCheck == false then\n                if d.lastTick > 0 then\n                    d.nextTick = d.lastTick + 30\n                else\n                    d.nextTick = d.exp + 30\n                    d.lastTick = d.exp\n                end\n                \n                if d.dur ~= 30 then\n                    d.exp = d.lastTick + 30\n                    d.dur = 30\n                end\n                \n                local remains = d.expDot - GetTime()\n                \n                d.durDot = remains > 9 and 39 or 30 + remains\n                d.expDot = GetTime() + d.durDot\n            end\n            \n        end\n        \n        if event == \"SPELL_PERIODIC_DAMAGE\" or event == \"SPELL_PERIODIC_MISSED\" then\n            local d = aura_env.Doom[dest]\n            \n            for i = 1,40 do \n                local nameplateCheck = false \n                if UnitGUID(\"nameplate\"..i) == dest then\n                    nameplateCheck = true\n                    local npd, npe = select(5, WA_GetUnitDebuff(\"nameplate\"..i, 265412, \"PLAYER\"))\n                    if now + 30 < d.expDot then\n                        d.exp = npe\n                        d.dur = npd\n                    else\n                        d.exp = npe\n                        d.dur = npd\n                    end\n                    \n                    d.lastTick = now\n                    d.nextTick = now + 30\n                    break\n                end\n            end\n            if nameplateCheck == false then\n                if now + 30 < d.expDot then\n                    d.exp = now + 30\n                    d.dur = d.exp - now\n                else\n                    d.exp = d.expDot\n                    d.dur = d.expDot - now\n                end\n                \n                d.lastTick = now\n                d.nextTick = now + 30\n            end\n        end\n        \n        if event == \"SPELL_AURA_REMOVED\" then\n            local d = aura_env.Doom[dest]\n            d.exp, d.dur, d.expDot, d.durDot, d.lastTick, d.nextTick = 0, 0, 0, 0, 0, 0\n        end\n        \n        WeakAuras.ScanEvents(\"DOOM_UPDATE\")\n        \n        return true    \n    end   \nend",
