				["init"] = {
					["custom"] = "local r = aura_env.region\nlocal b = r.bar\nlocal aura_env = aura_env\naura_env.powerCasts = {-- generation, Fortres of the mind\n    [GetSpellInfo(280711)] = {50}, --Dark Ascension\n    [GetSpellInfo(263346)] = {30}, --Dark Void\n    [GetSpellInfo(15407)] = {3, true}, --Mind Flay\n    [GetSpellInfo(8092)] = {12, true}, --Mind Blast\n    [GetSpellInfo(205351)] = {15}, --Shadow Word: Void\n    [GetSpellInfo(34914)] = {6} --Vampiric Touch\n}\naura_env.getPowerFromCast = function()\n    local cast = UnitCastingInfo(\"player\") or UnitChannelInfo(\"player\") or nil\n    if not cast then\n        return 0\n    end\n    local data = aura_env.powerCasts[cast] or nil\n    if not data then\n        return 0\n    end\n    \n    local power = data[1] or 0 \n    if power then\n        if select(4, GetTalentInfo(1, 1, 1)) and data[2] then\n            power = power + power * 0.2 \n        end\n        if WA_GetUnitBuff(\"player\", 193223) then power = power * 2 end\n    end\n    return power\nend\naura_env.spiritsData = {}\n--power from AS\naura_env.getPowerFromAS = function()\n    if not IsPlayerSpell(155271) then --no AS talented\n        return 0\n    end\n    local time = GetTime()\n    local power = 0\n    for guid,_ in pairs(aura_env.spiritsData) do\n        --If we haven't had a new SA spawn in 10sec, that means all SAs that are out have hit the target (usually), or, the target disappeared.\n        if time - aura_env.spiritsData[guid].lastUpdate > 10 then\n            aura_env.spiritsData[guid] = nil\n        else\n            power = power + aura_env.spiritsData[guid].count * 2\n        end\n    end\n    if WA_GetUnitBuff(\"player\", 193223) then power = power * 2 end\n    return power\nend\naura_env.castPower = aura_env.getPowerFromCast()\naura_env.power = UnitPower(\"player\")\naura_env.powerMax = UnitPowerMax(\"player\")\naura_env.asPower = aura_env.getPowerFromAS()\naura_env.powerTextUpdate = function()\n    local a = aura_env\n    local power = UnitPower(\"player\", Enum.PowerType.Insanity)\n    local returnString = power\n    local aspower = a.getPowerFromAS()\n    local castPower = a.getPowerFromCast()\n    if castPower > 0 then\n        returnString = returnString..\"+\"..castPower\n    end\n    if aspower and aspower > 0 then\n        returnString = returnString..\"|cFFFF00FF+\"..aspower\n    end\n    return returnString \nend\nif not r.text3 then\n    local text3 = r.bar:CreateFontString(nil, \"OVERLAY\")\n    r.text3 = text3\n    r.text3:SetJustifyH(\"CENTER\")\n    r.text3:SetJustifyV(\"MIDDLE\")\n    r.text3:SetPoint(\"CENTER\", r.bar, \"CENTER\")\n    r.text3:Show()\n    r.text3:SetShadowOffset(1, -1)\n    r.text3:SetFont(r.text:GetFont())\n    r.text3:SetTextColor(r.text:GetTextColor())    \n    r.text3:SetText(aura_env.powerTextUpdate())\n    \nend\n\nif not b.sparkVF then\n    local sparkVF = b:CreateTexture(nil, \"ARTWORK\")\n    sparkVF:SetDrawLayer(\"ARTWORK\", 4)\n    sparkVF:SetTexture(b:GetTexture())\n    sparkVF:SetColorTexture(1,1,1,1)\n    sparkVF:SetWidth(1);\n    sparkVF:SetHeight(b:GetHeight());\n    sparkVF:ClearAllPoints()\n    sparkVF:Show()\n    b.sparkVF = sparkVF\nend\naura_env.VFAnchor = function()\n    local r = aura_env.region\n    local bar = aura_env.region.bar\n    local powerMax = aura_env.powerMax\n    local pix = bar:GetWidth() / powerMax\n    local VECost = select(4, GetTalentInfo(7, 1, 1)) and 60 or 90\n    local offsetVF = pix * VECost\n    --print(90/bar:GetWidth())\n    --print(offsetVF)\n    b.sparkVF:SetPoint(\"CENTER\", bar, \"LEFT\", offsetVF, 0)\nend\naura_env.VFAnchor()\n\n\n",
